var TREK={};TREK.isRequired=function(){var a=window.navigator.platform;switch(a){case"iPod":return!0;case"iPhone":return!0;case"iPad":return!0;case"iPhone Simulator":return!0;case"iPod Simulator":return!0;case"iPad Simulator":return!0;default:return!1}},TREK.setVisible=function(a){a?document.head.innerHTML+='<meta name="apple-mobile-web-app-capable" content="no">':document.head.innerHTML+='<meta name="apple-mobile-web-app-capable" content="yes">'},TREK.setStatusColor=function(a){document.head.innerHTML+='<meta name="apple-mobile-web-app-status-bar-style" content="'+a+'">'},TREK.setTitle=function(a){document.head.innerHTML+='<meta name="apple-mobile-web-app-title" content="'+a+'">'},TREK.addIcon=function(a,b){null==b?document.head.innerHTML+='<link rel="apple-touch-icon" href="'+a+'">':document.head.innerHTML+='<link rel="apple-touch-icon" sizes="'+b+'" href="'+a+'">'};